// Adventures.js
import React, { useEffect, useState } from 'react';
import AdventureAlbum from './AdventureAlbum';
import "./master.css";
import "./adventures.css"

const Adventures = () => {
    const [displayedText, setDisplayedText] = useState(''); // State to store the text being typed
    const fullText = "My favorite adventure is going Skydiving in Utah! I'm visiting Hobbiton in New Zealand next!";

    useEffect(() => {
        // Typewriter effect logic
        let index = 0;
        const typingSpeed = 100; // Adjust the typing speed (in ms)

        const typeWriter = () => {
            if (index < fullText.length) {
                setDisplayedText((prev) => prev + fullText.charAt(index));
                index++;
                setTimeout(typeWriter, typingSpeed); // Delay between characters
            }
        };

        typeWriter(); // Start the typing effect when the component mounts


    }, [fullText]);

    return (
        <div>
            <div className="page-warmer">
                <img className="page-photo" src="../images/adventures/skydive-moab.png" alt="skydive-moab" loading="lazy" />
                <h1 className="page-text">{displayedText}</h1> {/* Display the typing text */}
            </div>
            <AdventureAlbum />
        </div>
    );
}

export default Adventures;
