// Adventures.js
import React from 'react';
import AdventureAlbum from './AdventureAlbum';
import useTypewriter from './UseTypeWriter';
import "./master.css";
import "./adventures.css";

const Adventures = ({ selectedLanguage }) => {
    // Define translations for the intro text
    const textTranslations = {
        English: "My favorite adventure is going Skydiving in Utah! I'm visiting Hobbiton in New Zealand next!",
        中文: "我最喜歡的冒險是去猶他州跳傘！我接下來要去紐西蘭看哈比屯！"
    };

    const fullText = textTranslations[selectedLanguage] || textTranslations['English'];
    const displayedText = useTypewriter(fullText, 20, selectedLanguage);

    return (
        <div>
            <div className="page-warmer">
                <img className="page-photo" src="../images/adventures/skydive-moab.png" alt="Skydive Moab" loading="lazy" />
                <h1 className="page-text">{displayedText}</h1>
            </div>
            <AdventureAlbum selectedLanguage={selectedLanguage} />  {/* Pass selectedLanguage */}
        </div>
    );
}

export default Adventures;
