// components/ProjectAlbum.js
import React from 'react';
import ProjectEntry from './ProjectEntry';
import entries from './data/adventure-entries';
import './master.css';
import './projects.css';

class ProjectAlbum extends React.Component {

    render() {

        return (
            <div className= "flex-container">
            {
                entries.itemlist.map((photo) => {
                    return <ProjectEntry key={photo.src} src={photo.src} caption={photo.caption} date={photo.date} genre={photo.genre} summary={photo.summary}/>
                })
            }
            </div>
        )
    }
};

export default ProjectAlbum;
