// AdventurePost.js
import React from 'react';
import { useParams } from 'react-router-dom';
import entries from './data/adventure-entries';
import './adventurePost.css';

const AdventurePost = () => {
    const { slug } = useParams();

    // Find the corresponding adventure entry by its slug
    const adventure = entries.itemlist.find(entry => entry.slug === slug);

    if (!adventure) {
        return <div>Post not found</div>;
    }

    return (
        <div className="adventure-post">
            <h1>{adventure.caption}</h1>
            <img src={`../images/adventures/${adventure.src}`} alt={adventure.caption} />
            <div className="adventure-details">
                <p>Date: {adventure.date}</p>
                <p>Location: {adventure.location}</p>
                {/* Here you can write your detailed blog post */}
                <p>{adventure.blogPost}</p>
            </div>
        </div>
    );
}

export default AdventurePost;
