// components/ProjectEntry.js
import React from 'react';
import './master.css';
import './projects.css';

class ProjectEntry extends React.Component {

  render() {
    const image_path = "../images/projects/" + this.props.src;

    return (
      <div className="project-entry-block">
        <a href={image_path}>
          <div className="project-text">
            <div className="blog-title">{this.props.caption}</div>
            <div className="date-and-genre">
              <p className="blog-date">{this.props.date}</p>
              <p className="blog-genre">{this.props.genre}</p>
            </div>
            <p className="blog-summary">{this.props.summary}</p>
          </div>
          <img src={this.props.src} alt={this.props.src} className="blog-image" />
        </a>
      </div>
    );
  }
}

export default ProjectEntry;
