// AdventureAlbum.js
import React from 'react';
import AdventureEntry from './AdventureEntry';
import entries from './data/adventure-entries';

const AdventureAlbum = ({ selectedLanguage }) => {
    return (
        <div className="flex-container">
            {
                entries.itemlist.map((adventure) => {
                    return (
                        <AdventureEntry 
                            key={adventure.src}
                            src={adventure.src}
                            location={adventure.location[selectedLanguage]}
                            date={adventure.date}
                            caption={adventure.caption[selectedLanguage]}
                            slug={adventure.slug}
                            genre={adventure.genre[selectedLanguage]}
                            title={adventure.title[selectedLanguage]}
                            dates={adventure.dates}
                            blogPost={adventure.blogPost}
                            selectedLanguage={selectedLanguage}  // Pass language for blog posts
                        />
                    );
                })
            }
        </div>
    );
}

export default AdventureAlbum;
