// components/ProjectAlbum.js
import React, { useRef, useEffect, useState } from 'react';
import ProjectEntry from './ProjectEntry';
import entries from './data/adventure-entries';
import './master.css';
import './projects.css';

const ProjectAlbum = () => {
  const scrollContainerRef = useRef(null);
  const [scrollAmount, setScrollAmount] = useState(0);
  const speed = 2; // Adjusted speed for faster scrolling

  // Clone entries to give the appearance of an infinite loop
  const clonedEntries = [...entries.itemlist, ...entries.itemlist]; // Double the entries

  useEffect(() => {
    const autoScroll = () => {
      if (scrollContainerRef.current) {
        const maxScroll = scrollContainerRef.current.scrollWidth / 2; // Loop back in the middle of cloned entries
        setScrollAmount((prevScroll) => {
          if (prevScroll >= maxScroll) {
            return 0; // Reset to the start of the container
          }
          return prevScroll + speed; // Continue scrolling
        });
      }
    };

    const interval = setInterval(autoScroll, 10); // Faster interval for smooth and faster scrolling

    return () => clearInterval(interval); // Cleanup on component unmount
  }, [speed]);

  useEffect(() => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollLeft = scrollAmount;
    }
  }, [scrollAmount]);

  return (
    <div className="scroll-container" ref={scrollContainerRef}>
      <div className="scroll-content">
        {clonedEntries.map((project, index) => (
          <div className="fade-in" key={`${project.src}-${index}`}>
            <ProjectEntry
              src={project.src}
              location={project.location}
              date={project.date}
              caption={project.caption}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProjectAlbum;
