// components/ProjectEntry.js
import React from 'react';
import './master.css';
import './projects.css';

class ProjectEntry extends React.Component {

  render() {

    const image_path = "../images/projects/" + this.props.src;

    return (
      <div className="project-entry-block">
        <a href={image_path}>
          {/* <div className="project-text"> */}
            <div className="blog-title">{this.props.caption}</div>
            {/* <div className="date-and-genre"> */}
              <div className="blog-date">{this.props.date}</div>
              <div className="blog-genre">{this.props.genre}</div>
            {/* </div> */}
            <div className="photo-caption">{this.props.summary}</div>
          {/* </div> */}
          <img className="photo-image" src={this.props.src} alt={this.props.src}/>
        </a>
      </div>
    );
  }
}

export default ProjectEntry;
