// components/Projects.js
import React, { useRef, useEffect, useState } from 'react';
import ProjectAlbum from './ProjectEntry';
import entries from './data/adventure-entries'; // Adjust path if needed
import './master.css';

const Projects = () => {
    return (
        <div>
            <div className="page-warmer">
                <img className="page-photo" src="../images/skydive-moab.png" alt="../images/skydive-moab.png"/>
                <h1 className="page-text">I recently checked off skydiving from my bucket list, I'll be visiting the Hobbiton in New Zealand soon!</h1>
              </div>
          <ProjectAlbum/>
        </div>
      );

  const scrollContainerRef = useRef(null);
  const [scrollAmount, setScrollAmount] = useState(0);
  const speed = 10; // Control the scroll speed

  useEffect(() => {
    const autoScroll = () => {
      if (scrollContainerRef.current) {
        setScrollAmount((prevScroll) => {
          const maxScroll = scrollContainerRef.current.scrollWidth - scrollContainerRef.current.offsetWidth;
          if (prevScroll >= maxScroll) {
            return 0; // Loop back to the start
          }
          return prevScroll + speed; // Continue scrolling
        });
      }
    };

    const interval = setInterval(autoScroll, 20); // Adjust the interval for smoother scrolling

    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  useEffect(() => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollLeft = scrollAmount;
    }
  }, [scrollAmount]);

  return (
    <div className="scroll-container" ref={scrollContainerRef}>
      <div className="scroll-content">
        {entries.itemlist.map((project) => (
          <ProjectEntry
            key={project.src}
            src={project.src}
            location={project.location}
            date={project.date}
            caption={project.caption}
          />
        ))}
      </div>
    </div>
  );
};

export default Projects;
