import React, { useEffect } from 'react';
import './master.css'; 

const Banner = ({ logoSrc, overlayLogoSrc, bannerText }) => {
  useEffect(() => {
    const handleScroll = () => {
      const bannerLogo = document.querySelector('.banner-logo');
      const bannerOverlayLogo = document.querySelector('.banner-overlay-logo');
      const scrollPosition = window.scrollY;

      // Calculate the degree of transformation based on scroll position
      const scaleValue = Math.min(1, 1 + scrollPosition / 1000); // Adjust the divisor to control the scaling speed
      const rotateValue = scrollPosition % 360; // Rotate based on scroll position

      // Apply transformations
      if (bannerLogo) {
        bannerLogo.style.transform = `scale(${scaleValue}) rotate(${rotateValue}deg)`;
      }
      if (bannerOverlayLogo) {
        bannerOverlayLogo.style.transform = `scale(${scaleValue})`;
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="banner-container">
      <div className="banner-image-overlay">
        <img className="banner-logo" src={logoSrc} alt="Banner Logo" />
        <img className="banner-overlay-logo" src={overlayLogoSrc} alt="Overlay Logo" />
      </div>
      <div className="banner-header-text">
        <h1>{bannerText}</h1>
      </div>
    </div>
  );
}

export default {Banner, Banner1};
