// components/ProjectEntry.js
import React from 'react';
import './master.css';
import './projects.css';

class ProjectEntry extends React.Component {
  render() {
    const image_path = "../images/projects/" + this.props.src;
    const { caption, genre, selectedLanguage } = this.props;

    return (
      <div className="project-container">
        <div className="project-entry-block">
          <a href={image_path}>
            <img className="project-img" src={image_path} alt={image_path} loading="lazy" />
            {/* Use selectedLanguage to choose the correct translation */}
            <div className="project-caption">{caption[selectedLanguage]}</div>
            <div className="project-genre">{genre[selectedLanguage]}</div>
          </a>
        </div>
      </div>
    );
  }
}

export default ProjectEntry;
