// AdventurePost.js
import React from 'react';
import { useParams } from 'react-router-dom';
import entries from './data/adventure-entries';
import './adventures.css';

const AdventurePost = () => {
    const { slug } = useParams();

    // Find the corresponding adventure entry by its slug
    const adventure = entries.itemlist.find(entry => entry.slug === slug);

    if (!adventure) {
        return <div>Post not found</div>;
    }

    return (
        <div className="adventure-post">
            <img className="adventure-banner" src={`../images/adventures/${adventure.banner}`} alt={adventure.caption} />
            <div className="adventure-poster">
                <div className="adventure-genre">{adventure.genre}</div>
                <div className="adventure-title">{adventure.title}</div>
                <div className="adventure-dates">{adventure.dates}</div>
                <div className="adventure-blog-content">
                {/* Loop through the blog content and render paragraphs and images */}
                {adventure.blogPost.map((content, index) => {
                    if (content.type === 'paragraph') {
                        return <p key={index}>{content.text}</p>;
                    } else if (content.type === 'image-3') {
                        return <div className="image-3">
                            <img key={index} src={`../images/adventures/${content.src1}`} alt={content.alt1} className="adventure-sub-img" />
                            <img key={index} src={`../images/adventures/${content.src2}`} alt={content.alt2} className="adventure-sub-img" />
                            <img key={index} src={`../images/adventures/${content.src3}`} alt={content.alt3} className="adventure-sub-img" />
                        </div>
                    } else if (content.type === 'image') {
                        return <img key={index} src={`../images/adventures/${content.src}`} alt={content.alt} className="adventure-sub-img" />;
                    } else {
                        return null;
                    }
                })}
            </div>
            </div>

        </div>
    );
}

export default AdventurePost;
