// components/ProjectAlbum.js
import React from 'react';
import ProjectEntry from './ProjectEntry';
import entries from './data/project-entries';
import './master.css';
import './projects.css';

class ProjectAlbum extends React.Component {
    render() {
        const { selectedLanguage } = this.props; // Get selected language

        return (
            <div className="flex-container">
                {
                    entries.itemlist.map((project) => {
                        return (
                            <ProjectEntry
                              key={project.src}
                              src={project.src}
                              caption={project.caption}
                              genre={project.genre}
                              selectedLanguage={selectedLanguage} // Pass selectedLanguage to ProjectEntry
                            />
                        );
                    })
                }
            </div>
        );
    }
}

export default ProjectAlbum;
