import React, { useEffect, useState } from 'react';
import './banner.css'; 

// Custom hook for the typewriter effect
const useTypewriter = (fullText, typingSpeed = 20) => {
  const [displayedText, setDisplayedText] = useState('');

  useEffect(() => {
    let index = 0;
    const intervalId = setInterval(() => {
      if (index < fullText.length) {
        setDisplayedText((prev) => prev + fullText.charAt(index));
        index++;
      } else {
        clearInterval(intervalId);
      }
    }, typingSpeed);

    return () => clearInterval(intervalId);
  }, [fullText, typingSpeed]);

  return displayedText;
};

const Banner = () => {
  const text0 = useTypewriter("  Hi, my name is Izzy. I love exploring the world around me. Welcome to my website!");
  const text1 = useTypewriter(" Truth, improvement, and altruism fuel my passion as a multidisciplinary engineer.");
  const text2 = useTypewriter(" In my free time, I love playing music and exploring natural wonders with my camera!");

  useEffect(() => {
    const handleScroll = () => {
      const banners = document.querySelectorAll('.banner-logo');
      const scrollPosition = window.scrollY;

      banners.forEach((banner, index) => {
        const overlay = document.querySelector(`.banner-overlay-logo-${index}`);
        const scaleValue = Math.min(1, 1 + scrollPosition / 1000);
        const rotateValue = scrollPosition % 720;

        banner.style.transform = `scale(${scaleValue}) rotate(${index % 2 === 0 ? rotateValue : -rotateValue}deg)`;
        overlay.style.transform = `scale(${scaleValue})`;
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const bannerData = [
    { text: text0, logo: "../images/izzy-pirate.png", overlay: "../images/izzy-pirate-overlay.png" },
    { text: text1, logo: "../images/izzy-professional.png", overlay: "../images/izzy-professional-overlay.png" },
    { text: text2, logo: "../images/izzy-photographer.png", overlay: "../images/izzy-photographer-overlay.png" }
  ];

  return (
    <div>
      {bannerData.map((banner, index) => (
        <div key={index} className="banner-container">
          <div className="banner-image-overlay">
            <img className={`banner-logo banner-logo-${index}`} src={banner.logo} alt="Izzy Logo" loading="lazy" />
            <img className={`banner-overlay-logo-${index}`} src={banner.overlay} alt="Overlay Logo" loading="lazy" />
          </div>
          <div className="banner-header-text">
            <h1>{banner.text}</h1>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Banner;
