import React, { useEffect } from 'react';
import './master.css'; 

const Banner = () => {
    useEffect(() => {
      const handleScroll = () => {
        const bannerLogo0 = document.querySelector('.banner-logo');
        const bannerOverlayLogo0 = document.querySelector('.banner-overlay-logo');

        // const bannerLogo1 = document.querySelector('.banner-logo');
        // const bannerOverlayLogo1 = document.querySelector('.banner-overlay-logo');
        const scrollPosition = window.scrollY;

        // Calculate the degree of transformation based on scroll position
        const scaleValue = Math.min(1, 1 + scrollPosition / 1000); // Adjust the divisor to control the scaling speed
        const rotateValue = scrollPosition % 360; // Rotate based on scroll position

        // Apply transformations
        bannerLogo0.style.transform = `scale(${scaleValue}) rotate(${rotateValue}deg)`;
        bannerOverlayLogo0.style.transform = `scale(${scaleValue})`;
      };

      window.addEventListener('scroll', handleScroll);

      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, []);

    return (
        <div className="banner-container">
        <div className="banner-image-overlay">
            <img className="banner-logo0" src="../images/izzy-logo.png" alt="Izzy Logo"/>
            <img className="banner-overlay-logo-0" src="../images/izzy-logo-overlay-2.png" alt="Overlay Logo"/>
          </div>
        <div className="banner-header-text">
            <h1>Hi, my name is Izzy. I study engineering at Northestern. In my free time, I love playing music and exploring natural wonders with my camera!</h1>
        </div>
        </div>
    );
  }


export default Banner;
