// AdventureAlbum.js
import React from 'react';
import AdventureEntry from './AdventureEntry';
import entries from './data/adventure-entries';

const AdventureAlbum = ({ selectedLanguage }) => {
    // Log the selected language when AdventureAlbum is rendered
    console.log("Selected Language in AdventureAlbum:", selectedLanguage);

    return (
        <div className="flex-container">
            {
                entries.itemlist.map((adventure) => {
                    // Check if the selected language exists in the caption translations
                    const adventureCaption = adventure.caption[selectedLanguage] || adventure.caption['English'];

                    // Log the caption being used and selected language
                    console.log("Adventure Caption:", adventureCaption);
                    console.log("Adventure Caption Language:", selectedLanguage);

                    return (
                        <AdventureEntry
                            key={adventure.src}
                            src={adventure.src}
                            location={adventure.location}
                            date={adventure.date}
                            caption={adventureCaption}
                            slug={adventure.slug}
                            genre={adventure.genre}
                            title={adventure.title}
                            dates={adventure.dates}
                        />
                    );
                })
            }
        </div>
    );
}

export default AdventureAlbum;
