// AdventureEntry.js
import React from 'react';
import { Link } from 'react-router-dom';

const AdventureEntry = ({ src, caption, location, date, slug, genre, title, dates, selectedLanguage }) => {
    return (
        <div className="adventure-entry">
            <Link to={`/adventures/${slug}`}>
                <img src={`../images/adventures/${src}`} alt={title} loading="lazy" />
                <div className="adventure-info">
                    <h2>{title}</h2>
                    <p>{caption}</p>
                    <p>{location}</p>
                    <p>{genre}</p>
                    <p>{dates}</p>
                </div>
            </Link>
        </div>
    );
};

export default AdventureEntry;
