// AdventureAlbum.js
import React from 'react';
import AdventureEntry from './AdventureEntry';
import entries from './data/adventure-entries';

class AdventureAlbum extends React.Component {

    render() {

        return (
            <div className= "flex-container">
            {
                entries.itemlist.map((photo) => {
                    return <AdventureEntry key={photo.src} src={photo.src} location={photo.location} date={photo.date} caption={photo.caption} slug={adventure.slug}/>
                })
            }
            </div>
        )
    }
}

export default AdventureAlbum;
