// components/ProjectEntry.js

import React from 'react';
import './master.css';
import './projects.css';

class ProjectEntry extends React.Component {

  render() {

    const image_path = "../images/" + this.props.src;

    return (
      <div className="project-entry-block">
        <a href = {image_path}>
          <img className="project-img" src={image_path} alt={this.props.caption} />
          <img className="project-frame" src='../images/frame.png' alt="../images/frame.png" />
          <div className="project-caption">{this.props.caption}</div>
          <div className="project-date">{this.props.date}</div>
          <div className="project-location">{this.props.location}</div>
        </a>
      </div>
    );
  }
}

export default ProjectEntry;
