import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';import Banner from './components/Banner';
import AlbumMaker from './components/AlbumMaker';
import Header from './components/Header';
import Adventures from './components/Adventures';
import './App.css';

function App() {
  return (
    <div className="App">
      {/* <header className="App-header"> */}
        <Header/>
        {/* <Banner/>
        <div>
          <h1>Welcome to My Europe Travel Journal</h1>
          <h3>Izzy Huang</h3>
          <AlbumMaker/>
        </div>
      </header> */}
      <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/adventures" element={<Adventures />} />
          {/* Add other routes as needed */}
        </Routes>
    </div>
  );
}
const Home = () => (
  <div>
    <Banner/>
    <div>
      <h1>Welcome to My Europe Travel Journal</h1>
      <h3>Izzy Huang</h3>
      <AlbumMaker/>
    </div>
  </div>
);

export default App;
